def other_dims_as_x_2(q_0_0,q_1_1,q_2_2,q_2_0,q_2_1,x_0): return [q_2_1*x_0/(q_0_0*x_0 - q_1_1*x_0 + q_2_0)]
def k_mat_2(q_0_0,q_1_1,q_2_2,q_2_0,q_2_1): return [q_0_0*(q_0_0 - q_1_1)**2, 2*q_2_0*(q_0_0 - q_1_1)*(2*q_0_0 - q_1_1), q_2_0**2*(5*q_0_0 - 4*q_1_1) + q_2_1**2*(2*q_0_0 - q_1_1) + q_2_2*(q_0_0 - q_1_1)**2, 2*q_2_0*(q_2_0**2 + q_2_1**2 + q_2_2*(q_0_0 - q_1_1)), q_2_0**2*q_2_2]
